// popkinsLinkClasses returns CSS classes for nav links based on active state
func popkinsLinkClasses(href, activePath string) string {
	base := "block px-3 py-2.5 text-sm uppercase transition-all duration-200"

	// Check if this link is active
	isActive := href == activePath ||
		(strings.HasPrefix(activePath, href) && href != "/deployments" && len(href) > 20) ||
		(href == "/deployments" && activePath == "/deployments")

	if isActive {
		return base + " text-[#33FF00] bg-[#0D1A0D] border-l-2 border-[#33FF00] -ml-[2px] drop-shadow-[0_0_8px_#33FF00] font-bold"
	}
	return base + " text-[#666600] hover:text-[#33FF00] hover:bg-[#0D1A0D]"
}

// getPopkinsInitial returns the first character for avatar placeholder
