// Helper to format limits.
func formatLimit(n int) string {
	if n < 0 {
		return "unlimited"
	}
	return formatIntToString(n)
}

func formatIntToString(n int) string {
	s := ""
	for n > 0 {
		s = string(rune('0'+n%10)) + s
		n /= 10
	}
	if s == "" {
		return "0"
	}
	return s
}

